import getDefaultTemplateConfig from "../templates/index.js"

const getConfigTemplate = async ({ apps, config, navigations, features }) => {
  const  {
    apps: appsBase,
    config: configBase,
    navigations: navigationsBase,
    ...rest
  } = await getDefaultTemplateConfig();
  
  return {
    apps: { ...appsBase, ...apps },
    features,
    config: { ...configBase, ...config },
    navigations: { ...navigationsBase, ...navigations },
    ...rest
  };
};

export default getConfigTemplate;
